mapper.select:
Select ${idfield} as ID


		, ${item}


from ${tablename}

	1 = 1
	and id in

		'${ids[index]}'

Select ${idfield} as id


		, ${item}


from ${tablename}




				and ${k}   ${condmap[k]}




		and ( ${likefilter} )

Select ${idfield} as id,${codefield} as refcode , ${namefield} as refname


		, ${item}


from ${tablename}

	1 = 1

		and
		(
			${codefield} like #{keyword}
			or
			${namefield} like #{keyword}
		)




				and ${k} ${conditionQuoter[k]} #{condmap[${k}]}





			${cond}



		and ${idfield}  in

			'${ids[index]}'

Select ${idfield} as id,${codefield} as refcode , ${namefield} as refname

	,${fidfield}



		, ${item}


from ${tablename}

	1 = 1

		and ${fidfield} = #{fidvalue}


		and
		(
		${codefield} like #{keyword}
		or
		${namefield} like #{keyword}
		)





				and ${k} ${conditionQuoter[k]} #{condmap[${k}]}





			${cond}



		and ${idfield}  in

			'${ids[index]}'

Select ${idfield} as id,${codefield} as refcode , ${namefield} as refname ,${pidfield} as pid


		, ${item}


from ${tablename}

	1 = 1

		and
		(
		${codefield} like #{keyword}
		or
		${namefield} like #{keyword}
		)




				and ${k} ${conditionQuoter[k]} #{condmap[${k}]}





			${cond}



		and ${idfield}  in

			'${ids[index]}'

Select ${idfield} as id, ${codefield} as code,${namefield} as name from ${tablename}
1 = 1 and ${idfield} in
'${ids[index]}'

Select TO_CHAR(${idfield}) as id, ${codefield} as code,${namefield} as name
from ${tablename}

	1 = 1
	and ${idfield} in

		'${ids[index]}'

	and
	(
	${codefield} like #{keyword}
	or
	${namefield} like #{keyword}
	)
